'use strict';

const { getProperties } = require('../index.js');
const test = require('ava');

test('normal input', t => {
	let obj = { a: { b: { c: 1 } } };

	t.deepEqual(getProperties(obj), ['a.b.c']);
});

test('undefined obj', t => {
	let obj = { a: undefined };

	t.deepEqual(getProperties(obj), ['a']);
});

test('null obj', t => {
	let obj = { a: null };

	t.deepEqual(getProperties(obj), ['a']);
});

test('{} obj', t => {
	let obj = { a: {} };

	t.deepEqual(getProperties(obj), ['a']);
});

test('not obj', t => {
	let obj = "haha";

	t.deepEqual(getProperties(obj), []);
});

test('more obj', t => {
	let obj = { a: { b: { c: 1, d: 3 } }, e: { f: 2, g: { h: { i: { j: 4, k: 5 } } } } };

	t.deepEqual(getProperties(obj), ['a.b.c', 'a.b.d', 'e.f', 'e.g.h.i.j', 'e.g.h.i.k']);
});
